import GrapesJS from 'grapesjs';
import gjsBasicBlocks from 'grapesjs-blocks-basic';
import Head from 'next/head';
import { useEffect, useState } from 'react';
import { GrapesPluginType, IStorageManager } from './GrapeType';

interface IProps {
  id: string;
  // Preset and plugin options
  webpage: boolean;
  newsletter: boolean;
  plugins: GrapesPluginType[];
  // Components
  components: any[];
  blocks: any[];
  // Editor configurations
  storageManager: IStorageManager;
  blockManager: {};
  styleManager: IStyleManager;
}
export default ({
  id = 'grapesjs-react-editor',
  components = [],
  blocks = [],
  plugins = []
}) => {
  const [editor, setEditor] = useState(null);

  useEffect(() => {
    if (!editor) {
      let eplugins = [...plugins, gjsBasicBlocks];
      const e = GrapesJS.init({
        container: `#${id}`,
        fromElement: true,
        eplugins
      });
      console.log(e);

      const defaultType = e.DomComponents.getType('default');
      const defaultModel = defaultType.model;
      const defaultView = defaultType.view;
      components.forEach((component: any) => {
        e.DomComponents.addType(component.type, {
          model: defaultModel.extend(
            {
              defaults: Object.assign({}, defaultModel.prototype.defaults)
            },
            {
              isComponent: component.isComponent.bind(this)
            }
          ),
          view: defaultView.extend({
            events: {
              ...component.events
            },
            render: component.render.bind(this)
          })
        });
      });
      blocks.forEach((block: any) => {
        e.BlockManager.add(block.id, block);
      });
      setEditor(e);
    } else {
      if (document) {
        document.getElementById(id).append(editor.render());
      }
    }

    return function cleanup() {
      if (editor) {
        editor.destroy();
        GrapesJS.editors = GrapesJS.editors.filter(e => e !== editor);
      }
    };
  }, []);

  return (
    <div id={id}>
      <Head>
        <link rel='stylesheet' href='/static/grapesjs/css/grapes.min.css' />
      </Head>
    </div>
  );
};
